import Link from "next/link";
import fetch from "isomorphic-unfetch";
export default class Footer extends React.Component {
    constructor() {
        super();
        this.state = {
            show: [],
        };
    }

    componentDidMount() {
        fetch("https://plant29.store/api/kategori")
            .then((res) => res.json())
            .then((data) => {
                this.setState({ show: data });
            });
    }
    render() {
        return (
            <>
                <footer
                    className="footer bg-color pt--70 pt-xs--60"
                    data-bg-color="#f4f8fa"
                    style={{ backgroundColor: "rgb(244, 248, 250)" }}
                >
                    <style jsx>{`
                        .logo {
                            width: 80px;
                        }
                        .address-widget {
                            padding-top: 20px;
                        }
                        .address-widget a {
                            font-size: 15px;
                            padding-top: 10px;
                        }
                        .address-widget p {
                            font-size: 20px;
                            font-weight: bold;
                        }
                        address {
                            font-size: 15px;
                        }
                        .sidenav {
                            position: fixed;
                            z-index: 9;
                            left: 0;
                            height: 100vh;
                            box-shadow: 0 0 10px #00000038;
                            top: 0;
                            background: #fff;
                            width: 0;
                            display: none;
                        }
                        .flaticon-cross {
                            font-size: 30px;
                            position: absolute;
                            right: 20px;
                        }
                        aside .mainmenu {
                            padding-top: 100px;
                            padding-left: 20px;
                            overflow: auto;
                        }
                        aside .mainmenu li {
                            font-size: 30px;
                        }
                        .social a {
                            font-size: 15px;
                        }
                        a:hover {
                            text-decoration: none !important;
                        }
                        .footer-menu li a span {
                            font-size: 20px;
                        }
                        @media only screen and (max-width: 999px) {
.address-widge{
   font-size: 15px;
}
address {
 font-size: 13px;
}
.footer-menu > li a span {
    font-size: 15px !important;
}
}
                    `}</style>
                    <div className="container">
                        <div className="row border-bottom pb--60 pb-sm--28 pb-xs--49">
                            <div className="col footer-column-1 mb-sm--42">
                                <div className="footer-widget">
                                    <div className="textwidget">
                                        <figure className="footer-logo mb--10">
                                            <img
                                                src="/assets/img/logo.png"
                                                alt="Logo"
                                                className="logo"
                                            />
                                        </figure>
                                    </div>
                                    <div className="address-widget">
                                        <p>We’re Always Happy to Help</p>
                                        <address>
                                            Desa No.003, RT.001, Tamansari, Kec.
                                            Tamansari, Bogor, Jawa Barat 16610
                                        </address>
                                        <a href="tel:+62 897-2862-184">
                                            +62 897-2862-184
                                        </a>
                                        <a href="mailto:addzaki11@gmail.com">
                                            addzaki11@gmail.com
                                        </a>
                                    </div>
                                </div>
                            </div>

                            <div className="col footer-column-3 mb-sm--33">
                                <div className="footer-widget">
                                    <h3 className="widget-title mb--35 mb-sm--15">
                                        Information
                                    </h3>
                                    <ul className="footer-menu">
                                        <li>
                                            <Link href="/about" as="/about">
                                                <a>
                                                    <i className="fa fa-angle-right"></i>
                                                    <span>About Us</span>
                                                </a>
                                            </Link>
                                        </li>
                                        <li>
                                            <Link
                                                href="/shipping"
                                                as="/shipping"
                                            >
                                                <a>
                                                    <i className="fa fa-angle-right"></i>
                                                    <span>
                                                        Delivery Information
                                                    </span>
                                                </a>
                                            </Link>
                                        </li>

                                        <li>
                                            <Link href="/trem" as="/trem">
                                                <a>
                                                    <i className="fa fa-angle-right"></i>
                                                    <span>
                                                        Terms &amp; Conditions
                                                    </span>
                                                </a>
                                            </Link>
                                        </li>
                                    </ul>
                                </div>
                            </div>
                        </div>
                        <div className="row ptb--20">
                            <div className="col-12 text-center">
                                <p className="copyright-text">
                                    Plant29 &copy; 2020
                                </p>
                                <div className="social space-10">
                                    <a
                                        href="https://web.facebook.com/SuryaPlant29"
                                        target="_blank"
                                        rel="noopener noreferrer"
                                        className="social__link"
                                    >
                                        Facebook
                                    </a>

                                    <a
                                        href="https://www.instagram.com/surya_plant29/"
                                        target="_blank"
                                        rel="noopener noreferrer"
                                        className="social__link"
                                    >
                                        Instagram
                                    </a>
                                </div>
                            </div>
                        </div>
                    </div>

                </footer>

            </>
        );
    }
}
