import Layout from "../components/layout";
import fetch from "isomorphic-unfetch";
import React from "react";
import Link from "next/link";
import ReactPaginate from "react-paginate";
import { default as minifyCssString } from "minify-css-string";
import { NextSeo } from 'next-seo';
function truncate(str) {
	return str.length > 10 ? str.substring(0, 20) + "..." : str;
}
export default class Produk extends React.Component {
	 constructor() {
    super();
    this.state = {
      imageIndex: 0,
      data: [],
      gm: "",
      offset: 0,
      perPage: 3,
      currentPage: 0,
    };
    this.handlePageClick = this.handlePageClick.bind(this);
  }
	static async getInitialProps() {
		const res = await fetch("https://zmbpediabogor.store/api/catalogdata");
		const show = await res.json();
		   let gambarnya ='';
		let altgam = '';
      if(show.data.length){
      gambarnya = await show.ogimg; 
        altgam = await show.alt; 
    }
	   return { show, gambarnya, altgam }
	}



	  componentDidMount() {
    window.scrollTo(0, 0);
    var myArray = [];
    for (var i = 0; i < this.props.show.data.length; i++) {
      var value = this.props.show.data[i];
      var values = this.props.show.data[0];
      var num = value["id"];
      var namaproduk = value["namaproduk"];
      var harga = value["harga"];
      var gambar = value["gambar"];
      var sh = values["gambar"];
      var on = value["stok"];
      var kast = value["kat"];
      var stok;

      if (on === "true") {
        stok = "sale";
      } else {
        stok = "out of stock";
      }
      var ulr = window.location.href;
      var one = ulr.split("/")[4];
      var two = ulr.split("/")[5];
      this.setState({
        namekat: "zmbpediabogor - Katalog produk",
        ada: "yes",
        titles: "zmbpediabogor - Katalog produk",
        urls: "https://zmbpediabogor.store/product",
        gam: sh,
      });
      myArray.push({
        id: num,
        nama: namaproduk,
        gambar: gambar,
        harga: harga,
        stok: stok,
      });
    }

    if (myArray.length) {
      const slice = myArray.slice(
        this.state.offset,
        this.state.offset + this.state.perPage
      );
      const postData = slice.map((a) => (

								<div className="col-12 col-sm-6 col-lg-4 featureCol mb-7">
									<div className="border">
										<div className="imgHolder position-relative w-100 overflow-hidden">
										<img
											src={a.gambar}
											style={{
												height: "300px",
												objectFit: "cover",
												width: "100%",
											}}
											id="wax-img"
											alt={a.namaproduk}
											className="img-fluid"
										/>
									</div>
										<div className="text-center py-5 px-4">
											<span className="title d-block mb-2">
											<Link
                      href="/produk/[id]/[product]"
                      as={`/produk/${a.id}/${a.nama.replace(/\s/g,"-")}`}
                    >
                      <a> {truncate(a.nama)}</a>
                    </Link>
											</span>
											<span className="price d-block fwEbold">{a.harga}</span>
											
											{/*<span className="hotOffer green fwEbold text-uppercase text-white position-absolute d-block ml-8">Sale</span>*/}
										</div>
									</div>
								</div>
    ));

      this.setState({
        pageCount: Math.ceil(myArray.length / this.state.perPage),
        postData,
      });
    } else {
      this.setState({ ada: "no" });
    }
  }

  handlePageClick = (e) => {
    const selectedPage = e.selected;
    const offset = selectedPage * this.state.perPage;

    this.setState({
      currentPage: selectedPage,
      offset: offset,
    });
    this.receivedData(offset);
  };

  receivedData = (e) => {
    console.log(e);
    window.scrollTo(0, 0);
    var myArray = [];
    for (var i = 0; i < this.props.show.data.length; i++) {
      var value = this.props.show.data[i];
      var values = this.props.show.data[0];
      var num = value["id"];
      var namaproduk = value["namaproduk"];
      var harga = value["harga"];
      var gambar = value["gambar"];
      var sh = values["gambar"];
      var on = value["stok"];
      var kast = value["kat"];
      var stok;

      if (on === "true") {
        stok = "sale";
      } else {
        stok = "out of stock";
      }
      var ulr = window.location.href;
      var one = ulr.split("/")[4];
      var two = ulr.split("/")[5];

      myArray.push({
        id: num,
        nama: namaproduk,
        gambar: gambar,
        harga: harga,
        stok: stok,
      });
    }


 const slice = myArray.slice(e, e + this.state.perPage);
    const postData = slice.map((a) => (

								<div className="col-12 col-sm-6 col-lg-4 featureCol mb-7">
									<div className="border">
										<div className="imgHolder position-relative w-100 overflow-hidden">
										<img
											src={a.gambar}
											style={{
												height: "300px",
												objectFit: "cover",
												width: "100%",
											}}
											id="wax-img"
											alt={a.namaproduk}
											className="img-fluid"
										/>
									</div>
										<div className="text-center py-5 px-4">
											<span className="title d-block mb-2">
											<Link
                      href="/produk/[id]/[product]"
                      as={`/produk/${a.id}/${a.nama.replace(/\s/g,"-")}`}
                    >
                      <a> {truncate(a.nama)}</a>
                    </Link>
											</span>
											<span className="price d-block fwEbold">{a.harga}</span>
											
											{/*<span className="hotOffer green fwEbold text-uppercase text-white position-absolute d-block ml-8">Sale</span>*/}
										</div>
									</div>
								</div>


    	   ));

    this.setState({
